    private void submitActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_submitActionPerformed
        // declare an ArrayList to dynamically store department names
        ArrayList<String> Departments = new ArrayList<>();

        // db credentials
        String url = "jdbc:mysql://localhost:3306/employee_expenses";
        String uname = "APT_final_project";
        String pass = "kfc4life8";

        // connection object
        Connection con = null;
        try {
            // register JDBC driver
            Class.forName("com.mysql.cj.jdbc.Driver");

            // establish db connection
            con = DriverManager.getConnection(url, uname, pass);

            // instuction to update existing record in db (option 3)
            String sqlUpdateBudget = "UPDATE budget_management SET Budget = Budget + ? WHERE Department = ?";

            // create a prepared statement object to update budget records (option 3)
            PreparedStatement psUpdate = con.prepareStatement(sqlUpdateBudget);

            // create statement objects for executing department SQL queries
            String sqlQueryDept = "SELECT Department FROM budget_management";
            Statement st1 = con.createStatement();
            // exexuting query for existing departments and store the result in a ResultSet
            ResultSet resDept = st1.executeQuery(sqlQueryDept);
            // Iterate through result set and save Department data in arrayList
            while (resDept.next()) {
                String dept = resDept.getString("Department");
                Departments.add(dept); // add element to ArrayList
            }

            String dept;
            float amount;

            // input department and check its existence
            dept = department_input.getText();
            dept = existsDept(dept, Departments, this);
            if (dept == null) {
                department_input.setText("");
                department_input.requestFocus();
                return;
            }

            if (isFloat(amount_input.getText())) {
                amount = Float.parseFloat(amount_input.getText());
            } else {
                JOptionPane.showMessageDialog(this, "\nInvalid Amount!!!");
                amount_input.setText("");
                amount_input.requestFocus();
                return;
            }

            // set update parameters
            psUpdate.setFloat(1, amount);
            psUpdate.setString(2, dept);

            // execute the update operation
            int update = psUpdate.executeUpdate();

            // check if update was successful
            if (update > 0) {
                JOptionPane.showMessageDialog(this, "\nDepartment's budget has been increased successfully");
                // reset form
                department_input.setText("");
                amount_input.setText("");
            } else {
                JOptionPane.showMessageDialog(this, "\nFailed to update budget!!!");
            }
        } catch (SQLException | ClassNotFoundException se) {
            // handle errors for JDBC
            se.printStackTrace();
            // display error to user
            JOptionPane.showMessageDialog(null, "SQL Exception occurred: " + se.getMessage(), "Error", JOptionPane.ERROR_MESSAGE);
        } finally {
            // Finally block used to close resources
            try {
                if (con != null) {
                    con.close();
                }
            } catch (SQLException se) {
                se.printStackTrace();
                // display error to user
                JOptionPane.showMessageDialog(null, "SQL Exception occurred: " + se.getMessage(), "Error", JOptionPane.ERROR_MESSAGE);
            }
        }
    }//GEN-LAST:event_submitActionPerformed

