    // checks if Department exists in DB
    public static String existsDept(String dept, ArrayList<String> Departments, JFrame parentComp) {
        String realLabel = null;
        for (String check : Departments) {
            if (dept.toLowerCase().strip().equals(check.toLowerCase())) {
                realLabel = check;
                break;
            }
        }
        if (realLabel == null) {
            JOptionPane.showMessageDialog(parentComp, "ERROR:\n...Department does not exist!!");
        }
        return realLabel;
    }

    // checks if amount field is actually a float number
    public static boolean isFloat(String txt) {
        try {
            // Use regex to check if the input contains only digits and optional decimal point
            if (txt.matches("\\d*\\.?\\d*")) {
                // If input is valid, parse it as a float
                return true;
            }
        } catch (NumberFormatException e) {
            return false;
        }
        return false;
    }
